mapper.select:
select
id, title, author_uid, category_id, content, created_at, updated_at
from blog_articles


        and id = #{id}


        and title = #{title}


        and author_uid = #{authorUid}


        and category_id = #{categoryId}


        and content = #{content}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from blog_articles


        and id = #{id}


        and title = #{title}


        and author_uid = #{authorUid}


        and category_id = #{categoryId}


        and content = #{content}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
